mapper.select:
SELECT id
FROM users
WHERE id=#{id} AND pwd=#{pwd}

SELECT pwd, email, profile, TO_CHAR(regdate, 'YY.MM.DD HH24:MI') AS regdate
FROM users
WHERE id=#{id}

mapper.update:
UPDATE users
SET pwd=#{newPwd}
WHERE id=#{id} AND pwd=#{pwd}

UPDATE users
SET profile=#{profile}
WHERE id=#{id}

UPDATE users
SET email=#{email}
WHERE id=#{id}
